/************************
			Cart hacks
*************************/

/** 
 * Render control view and attach functions
 */
function spawnCartControls() {
	classifyProducts();
	sortProducts();
	const panel = $('#edit-group_cart'); // fieldset area
	const controls = `
		<div class="tm-controls" id="tm-cart-controls">
			<button id="cart-hide-unchecked" class="admin-btn">Hide unchecked</button>
			<button id="cart-show-all" class="admin-btn">Show all</button>
			<button id="cart-show-current" class="admin-btn">Show current</button>
			<select id="cart-select-seller" class="admin-sel">
				<option value="all" selected>all</option>
				<option value="adma">ADMA</option>
				<option value="ami">AMI</option>
				<option value="axi">AXI</option>
			</select>
		</div>`;
	panel.prepend(controls);

	// initiate control event handlers
	$('#cart-hide-unchecked').click(hideUncheckedProducts);
	$('#cart-show-all').click(showAllProducts);
	$('#cart-show-current').click(showCurrentProducts);
	$('#cart-select-seller').change(selectSeller);

	autoDetectSeller();

	console.log('cart functions added');
}

function hideDataAttr(attrName, attrValue) {
	$( `[${attrName}${(attrValue ? '='+attrValue : '')}]` ).hide();
}

function showDataAttr(attrName, attrValue) {
	$( `[${attrName}${(attrValue ? '='+attrValue : '')}]` ).show();
}

function addDataAttr(sel, attrName, attrValue) {
	sel.attr( attrName, attrValue );
}

/** hide all products except for selected seller */
function selectSeller(e) {
	switch(e.target.value) {
		case 'all':
			showDataAttr('data-product-seller');
			break;
		case 'adma':
			hideDataAttr('data-product-seller');
			showDataAttr('data-product-seller', 'adma');
			break;
		case 'ami':
			hideDataAttr('data-product-seller');
			showDataAttr('data-product-seller', 'ami');
			break;
		case 'axi':
			hideDataAttr('data-product-seller');
			showDataAttr('data-product-seller', 'axi');
		case 'default':
			console.log('selectSeller() default');
	}
}

function autoDetectSeller() {
	const pageName = $('#edit-title').val();
	let seller = '';
	if( pageName.split('-').length > 1 ) {
		seller = pageName.split('-')[1].trim();
	} else { seller = 'adma' }

	// trigger click on <select>
	$('#cart-select-seller')
		.find(`option[value="${seller.toLowerCase()}"]`)
		.prop('selected', true)
		.trigger('change');
}

/** append data attributes to product labels */
function classifyProducts() {
	const productRows = $('#edit-field-products-und .form-item');
	for( let row of Array.from(productRows) ) {

		const sku = $(row).find('label').text().substring(0, 15); // 15 character SKU
		const data = inClassCourseData[sku];

		if(data) {
			let seller = '';
			if( data['Product Code'].endsWith( 'AMI' ) ) { seller = 'ami'; }
			else if( data['Product Code'].endsWith( 'XI' ) ) { seller = 'axi'; }
			else { seller = "adma"; }

			const sel = $(row);
			addDataAttr( sel, 'data-product-name', data['Product Name'] );
			addDataAttr( sel, 'data-product-code', data['Product Code'] );
			addDataAttr( sel, 'data-product-location', data['Location'] );
			addDataAttr( sel, 'data-product-type', data['Product Type'] );
			addDataAttr( sel, 'data-product-subtype', data['Product Sub-type'] );
			addDataAttr( sel, 'data-product-date', data['Start Date'] );
			addDataAttr( sel, 'data-product-sku', sku );
			addDataAttr( sel, 'data-product-seller', seller );
		}
		// if checked, mark as unclassified but not inactive (mostly for online courses)
		else if( $(row).find('input').attr('checked') === 'checked' ) {
			addDataAttr( $(row), 'data-unclassified', true );
		}
		else { 
			addDataAttr( $(row), 'data-unclassified', true );
			addDataAttr( $(row), 'data-inactive', true );
		}
	}
}

/** hide un-ticked products */
function hideUncheckedProducts(e) {
	e.preventDefault();
	const rows = $('#edit-group_cart .form-item.form-type-checkbox');
	for ( let row of Array.from(rows) ) {
		if( $(row).find('input[checked="checked"]').length === 1 ) {
			console.log('checked item');
		}
		else { $(row).hide() }
	}
}

/** reveal all products */
function showAllProducts(e) {
	e.preventDefault();
	const rows = $('#edit-group_cart .form-item.form-type-checkbox');
	for( let row of Array.from(rows) ) {
		$(row).show();
	}
}

function showCurrentProducts(e) {
	e.preventDefault();
	hideDataAttr('data-unclassified=true');
	showDataAttr('data-sku');
}

/** alphabetise by product name */
function sortProducts() {
	console.log('sort start');
	const rows = $('#edit-field-products-und .form-item[data-unclassified!="true"]');
	let sortedProducts = Array.from(rows).sort(function(a,b) {
		const aName = $(a).attr('data-product-name');
		const bName = $(b).attr('data-product-name');

		return aName.localeCompare(bName);
	});
	// add unclassifieds
	const unclassifiedProducts = $('#edit-field-products-und .form-item[data-unclassified="true"]');

	$('#edit-field-products-und')
		.html( '' )
		.append(sortedProducts)
		.append(unclassifiedProducts)
	console.log('sort finish');
}


